import React from 'react';
import { useParams } from 'react-router-dom';
import QRCode from 'qrcode.react';
import './FinalTicket.css';

const FinalTicket = () => {
  const { id } = useParams();
  const bookingDetails = JSON.parse(decodeURIComponent(id));

  // Check if seatNumbers length matches quantity
  if (bookingDetails.quantity !== bookingDetails.seatNumbers.length) {
    // Show an alert if they don't match
    alert('The number of booked seats does not match the quantity.');
  }

  return (
    <div className="final-ticket">
      <nav className="fin-navbar"><h1>Your Ticket</h1></nav>
      
      <div className="ticket-content">
        <div className="qr-code">
          <QRCode value={JSON.stringify(bookingDetails)} />
        </div>
        <div className="ticket-details">
          <p>Movie Name: {bookingDetails.movieName}</p>
          <p>Date: {bookingDetails.date}</p>
          <p>Show Time: {bookingDetails.showTime}</p>
          <p>No. of Seats: {bookingDetails.seatNumbers.length}</p> {/* Display the length of seatNumbers */}
          <p>Seat Numbers: {bookingDetails.seatNumbers.join(', ')}</p>
          <p>Payment Method: {bookingDetails.paymentMethod}</p>
        </div>
      </div>
    </div>
  );
};

export default FinalTicket;
