import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './TicketBooking.css';

const TicketBooking = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [date, setDate] = useState('');
  const [showTime, setShowTime] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [seatNumbers, setSeatNumbers] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState('');

  const showTimes = [
    '06:00 - 09:00',
    '09:30 - 12:00',
    '12:30 - 15:30',
    '16:00 - 19:00',
    '19:30 - 22:30',
    '23:00 - 02:00',
    '02:30 - 05:30',
  ];

  const handleBooking = () => {
    const bookingDetails = {
      movieId: id,
      date,
      showTime,
      quantity,
      seatNumbers,
      paymentMethod,
    };
    const bookingDetailsString = JSON.stringify(bookingDetails);
    navigate(`/ticket/${encodeURIComponent(bookingDetailsString)}`);
  };

  const handleSeatChange = (index, value) => {
    const updatedSeatNumbers = [...seatNumbers];
    updatedSeatNumbers[index] = value;
    setSeatNumbers(updatedSeatNumbers);
  };

  const seatInputs = [];
  for (let i = 0; i < quantity; i++) {
    seatInputs.push(
      <input
        key={i}
        type="text"
        placeholder={`Seat ${i + 1}`}
        value={seatNumbers[i] || ''}
        onChange={(e) => handleSeatChange(i, e.target.value)}
      />
    );
  }

  return (
    <div className="ticket-booking">
      <nav className="tic-navbar"><h1 style={{color:'white'}}>Book Tickets</h1></nav>
      <div className='tic-box'>
      <label>
        Date:<br></br>
        <input
        className="ip"
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />
      </label>
      <label>
        Available Show Timings:<br></br>
        <select className="ip" value={showTime} onChange={(e) => setShowTime(e.target.value)}>
          <option value="">Select Show Time</option>
          {showTimes.map((time, index) => (
            <option key={index} value={time}>
              {time}
            </option>
          ))}
        </select>
      </label>
      <label>
        Number of Seats:<br></br>
        <input
        className="ip"
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          min="1"
        />
      </label>
      <div className="ip">
        {seatInputs}
      </div>
      <label>
        Payment Method:<br></br>
        <select
        className="ip"
          value={paymentMethod}
          onChange={(e) => setPaymentMethod(e.target.value)}
        >
          <option value="">Select Payment Method</option>
          <option value="credit-card">Credit Card</option>
          <option value="debit-card">Debit Card</option>
          <option value="paypal">PayPal</option>
        </select>
      </label>
      <button onClick={handleBooking}>Book Now</button>
      </div>
    </div>
  );
};

export default TicketBooking;
