import React from 'react';
import { useParams, Link } from 'react-router-dom';
import './MovieDetail.css';

const MovieDetail = () => {
  const { id } = useParams();
  const movie = { 
    title: `Movie ${id}`, 
    description: `Description for movie ${id}`, 
    imageUrl: `https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.shutterstock.com%2Fimage-vector%2Fmovie-time-neon-sign-bright-signboard-1303729567&psig=AOvVaw1cp2SMu2vnkWIQI9jLrFo2&ust=1720698577432000&source=images&cd=vfe&opi=89978449&ved=0CBEQjRxqFwoTCNDshu2znIcDFQAAAAAdAAAAABAE`,  // Example image URL
  };

  return (
    <div className="movie-detail">
      <div className="movie-info">
        <div className="title">
          <h1>{movie.title}</h1>
        </div>
        <div className="Box">
          <div className="card">
            <div className="image">
              <img src={movie.imageUrl} alt={movie.title} />
            </div>
            <div className="book-now">
              <Link to={`/book/${id}`} className="btn-book-now">Book Now</Link>
            </div>
          </div>
          <div className="description">
            <p>{movie.description}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MovieDetail;
