import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './TicketBooking.css';

const TicketBooking = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [selectedDate, setSelectedDate] = useState('');
  const [showTime, setShowTime] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState('');

  const dates = [
    '2024-07-10',
    '2024-07-11',
    '2024-07-12',
  ];

  const showTimes = [
    '06:00 - 09:00',
    '09:30 - 12:00',
    '12:30 - 15:30',
    '16:00 - 19:00',
    '19:30 - 22:30',
    '23:00 - 02:00',
    '02:30 - 05:30',
  ];

  const seatLayout = [
    ['A', [1, 2, 3, 4, 5, 6, 7, 8]],
    ['B', [1, 2, 3, 4, 5, 6, 7, 8]],
    ['C', [1, 2, 3, 4, 5, 6, 7, 8]],
    ['D', [1, 2, 3, 4, 5, 6, 7, 8]],
    ['E', [1, 2, 3, 4, 5, 6, 7, 8]],
  ];

  const handleSeatClick = (row, seat) => {
    const seatId = `${row}${seat}`;
    setSelectedSeats(prevSelectedSeats =>
      prevSelectedSeats.includes(seatId)
        ? prevSelectedSeats.filter(s => s !== seatId)
        : [...prevSelectedSeats, seatId]
    );
  };

  const handleBooking = () => {
    const bookingDetails = {
      movieId: id,
      selectedDate,
      showTime,
      quantity,
      selectedSeats,
      paymentMethod,
    };
    const bookingDetailsString = JSON.stringify(bookingDetails);
    navigate(`/ticket/${encodeURIComponent(bookingDetailsString)}`);
  };

  return (
    <div className="ticket-booking">
      <nav className="tic-navbar">
        <h1 style={{color: 'white'}}>Book Tickets</h1>
      </nav>
      <div className='tic-box'>
        <label>
          Select Date:<br></br>
          <div className="date-buttons">
            {dates.map((date, index) => (
              <button
                key={index}
                className={`date-button ${selectedDate === date ? 'selected' : ''}`}
                onClick={() => setSelectedDate(date)}
              >
                {new Date(date).toDateString()}
              </button>
            ))}
          </div>
        </label>
        <br></br>
        <label>
          Available Show Timings:<br></br>
          <select
            className="ip"
            value={showTime}
            onChange={(e) => setShowTime(e.target.value)}
          >
            <option value="">Select Show Time</option>
            {showTimes.map((time, index) => (
              <option key={index} value={time}>
                {time}
              </option>
            ))}
          </select>
        </label>
        <br></br>
        <label>
          Number of Seats:<br></br>
          <input
            className="ip"
            type="number"
            value={quantity}
            onChange={(e) => setQuantity(e.target.value)}
            min="1"
          />
        </label>
        <br></br>
        <div className="seat-grid">
          {seatLayout.map(([row, seats]) => (
            <div key={row} className="seat-row">
              <span>{row}</span>
              {seats.map(seat => {
                const seatId = `${row}${seat}`;
                return (
                  <button
                    key={seat}
                    className={`seat ${selectedSeats.includes(seatId) ? 'selected' : ''}`}
                    onClick={() => handleSeatClick(row, seat)}
                  >
                    {seat}
                  </button>
                );
              })}
            </div>
          ))}
        </div>
        <br></br>
        <label>
          Payment Method:<br></br>
          <select
            className="ip"
            value={paymentMethod}
            onChange={(e) => setPaymentMethod(e.target.value)}
          >
            <option value="">Select Payment Method</option>
            <option value="credit-card">Credit Card</option>
            <option value="debit-card">Debit Card</option>
            <option value="paypal">PayPal</option>
          </select>
        </label>
        <br></br>
        <button onClick={handleBooking} className='ticbutton'>Book Now</button>
      </div>
    </div>
  );
};

export default TicketBooking;
