import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import QRCode from 'qrcode.react';
import './FinalTicket.css';

const FinalTicket = () => {
  const { id } = useParams();
  const bookingDetails = JSON.parse(decodeURIComponent(id));
  const [ticketBookings, setTicketBookings] = useState([]);

  useEffect(() => {
    // Replace with your API call to fetch ticket bookings
    fetch('/api/ticketBookings')
      .then(response => response.json())
      .then(data => setTicketBookings(data))
      .catch(error => console.error('Error fetching ticket bookings:', error));
  }, []);

  const ticketBooking = ticketBookings.find(booking => booking.id === bookingDetails.id);

  return (
    <div className="final-ticket">
      <nav className="fin-navbar"><h1>Your Ticket</h1></nav>
      
      <div className="ticket-content">
        <div className="qr-code">
          <QRCode value={JSON.stringify(bookingDetails)} />
        </div>
        <div className="ticket-details">
          <p>Movie ID: {bookingDetails.movieId}</p>
          <p>Date: {ticketBooking ? ticketBooking.date : 'N/A'}</p>
          <p>Show Time: {bookingDetails.showTime}</p>
          <p>Quantity: {bookingDetails.quantity}</p>
          <p>Seat Numbers: {ticketBooking && Array.isArray(ticketBooking.seatNumbers) ? ticketBooking.seatNumbers.join(', ') : 'N/A'}</p>
          <p>Payment Method: {bookingDetails.paymentMethod}</p>
        </div>
      </div>
    </div>
  );
};

export default FinalTicket;
