import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './TicketBooking.css';

const TicketBooking = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [date, setDate] = useState('');
  const [showTime, setShowTime] = useState('');
  const [seatNumbers, setSeatNumbers] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState('');

  const showTimes = [
    '06:00 - 09:00',
    '09:30 - 12:00',
    '12:30 - 15:30',
    '16:00 - 19:00',
    '19:30 - 22:30',
    '23:00 - 02:00',
    '02:30 - 05:30',
  ];

  const handleBooking = () => {
    const bookingDetails = {
      movieId: id,
      date,
      showTime,
      quantity: seatNumbers.length,
      seatNumbers,
      paymentMethod,
    };
    const bookingDetailsString = JSON.stringify(bookingDetails);
    navigate(`/ticket/${encodeURIComponent(bookingDetailsString)}`);
  };

  const toggleSeatSelection = (seat) => {
    setSeatNumbers((prevSeats) =>
      prevSeats.includes(seat)
        ? prevSeats.filter((s) => s !== seat)
        : [...prevSeats, seat]
    );
  };

  const seatRows = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'J', 'K', 'L'];
  const seatCols = Array.from({ length: 24 }, (_, i) => i + 1);

  return (
    <div className="ticket-booking">
      <nav className="tic-navbar">
        <h1 style={{ color: 'white' }}>Book Tickets</h1>
      </nav>
      <div className="tic-box">
        <label>
          Date:
          <br />
          <div className="date-buttons">
            {/* Render date buttons dynamically (for example) */}
            {['2024-07-10', '2024-07-11', '2024-07-12'].map((d) => (
              <button
                key={d}
                className={`date-button ${date === d ? 'selected' : ''}`}
                onClick={() => setDate(d)}
              >
                {d}
              </button>
            ))}
          </div>
        </label>
        <br />
        <label>
          Available Show Timings:
          <br />
          <select className="ip" value={showTime} onChange={(e) => setShowTime(e.target.value)}>
            <option value="">Select Show Time</option>
            {showTimes.map((time, index) => (
              <option key={index} value={time}>
                {time}
              </option>
            ))}
          </select>
        </label>
        <br />
        <label>
          Payment Method:
          <br />
          <select
            className="ip"
            value={paymentMethod}
            onChange={(e) => setPaymentMethod(e.target.value)}
          >
            <option value="">Select Payment Method</option>
            <option value="credit-card">Credit Card</option>
            <option value="debit-card">Debit Card</option>
            <option value="paypal">PayPal</option>
          </select>
        </label>
        <br />
        <div className="seat-grid">
          {seatRows.map((row) => (
            <div key={row} className="seat-row">
              <span>{row}</span>
              {seatCols.map((col) => {
                const seat = `${row}${col}`;
                return (
                  <div
                    key={seat}
                    className={`seat ${seatNumbers.includes(seat) ? 'selected' : ''}`}
                    onClick={() => toggleSeatSelection(seat)}
                  >
                    {col}
                  </div>
                );
              })}
            </div>
          ))}
        </div>
        <br />
        <button onClick={handleBooking} className="ticbutton">
          Book Now
        </button>
      </div>
    </div>
  );
};

export default TicketBooking;
