import React from 'react';
import { useParams, Link } from 'react-router-dom';
import './MovieDetail.css';

const MovieDetail = () => {
  const { id } = useParams();
  const movies = [
    { id: 1, title: 'Kalki', description: 'Hero: Soori \n', imageUrl: 'https://cdn.123telugu.com/content/wp-content/uploads/2024/04/Kalki-2898-AD-1.jpg' },
    { id: 2, title: 'Garudan', description: 'Description for Garudan', imageUrl: 'https://assetscdn1.paytm.com/images/cinema/Garudan-05x750-74275de0-1bf1-11ef-bee8-8b90ac5cda97.jpg?format=webp&imwidth=322' },
    { id: 3, title: 'Maharaja', description: 'Description for Maharaja', imageUrl: 'https://m.media-amazon.com/images/M/MV5BMTE0ZjY3MTUtYzUwMy00ZWEzLTgwMTYtMjg3ZDE1NTMxOTE4XkEyXkFqcGc@._V1_.jpg' },
  ];
  const movie = movies.find(movie => movie.id === parseInt(id));

  return (
    <div className="movie-detail">
      <div className="movie-info">
       
        <div className="Box">
          <div className="card">
            <div className="image">
              <img src={movie.imageUrl} alt={movie.title} />
            </div>
            <div className="description">
              <p>{movie.description}</p>
              <div className="book-now">
                <Link to={`/book/${id}`} className="btn-book-now">Book Now</Link>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MovieDetail;
